import * as React from "react";
import { Link } from "react-router-dom";
import axios from "axios";
// import { getQAById } from "../../lib/axios";
// import { deleteUser } from "../../lib/axios";

interface IState {
  QA: any[];
}

export default class EditQA extends React.Component<any, IState> {
  constructor(props: any) {
    super(props);
    this.state = { QA: [] };
  }
  public componentDidMount(): void {

    axios.get(`http://localhost:9000/app/allQA`).then((data) => {
      this.setState({ QA: data.data });
    });
  }
  public editQA(id: any, question: any, answer: any) {
    axios.put(`${`http://localhost:9000/app/updateQA`}?id=${id}`, {question, answer}).then(function (data) {
      // handle success
      console.log({ Users: data.data });
      return data.data;
    })

  }

