import * as React from 'react';
import "./QAList.css";
import axios from 'axios';
import { getQAById } from '../../lib/axios';
import { getALLQA } from '../../lib/endpoints';

export interface IValues {
    [key: string]: any;
}
export interface IFormState {
    id: number,
    question: any;
    answer: any;
    values: IValues[];
    submitSuccess: boolean;
    loading: boolean;
}

class QAList extends React.Component<any, any , IFormState >{
    constructor(props: any) {
        super(props);
        this.state = {
            id: "",
            question: {},
            answer: {},
            values: [],
            loading: false,
            submitSuccess: false,
        }
    }

    public componentDidMount(): void {
        getALLQA({});
        // axios.get(`http://localhost:5000/QA/${this.state.id}`).then(data => {
        //     this.setState({ qa: data.data });
        // })
    }

    private processFormSubmission = async (e: React.FormEvent<HTMLFormElement>): Promise<void> => {
        e.preventDefault();
        this.setState({ loading: true });
        // axios.patch(`http://localhost:5000/QA/${this.state.id}`, this.state.values).then(data => {
        //     this.setState({ submitSuccess: true, loading: false })
        //     setTimeout(() => {
        //         this.props.history.push('/');
        //     }, 1500)
        // })
    }

    private setValues = (values: IValues) => {
        this.setState({ values: { ...this.state.values, ...values } });
    }
    private handleInputChanges = (e: React.FormEvent<HTMLInputElement>) => {
        e.preventDefault();
        this.setValues({ [e.currentTarget.id]: e.currentTarget.value })
    }

    public render() {
        const { submitSuccess, loading } = this.state;
        return (
            <div className="App">
                {this.state.question &&
                    <div>
                        < h1 >  List of QA</h1>


                        <div>
                            <div className={"col-md-12 form-wrapper"}>
                                <h2> Edit QA </h2>
                                {submitSuccess && (
                                    <div className="alert alert-info" role="alert">
                                        QA details has been edited successfully </div>
                                )}
                                <form id={"create-post-form"} onSubmit={this.processFormSubmission} noValidate={true}>
                                    <div className="form-group col-md-12">
                                        <label htmlFor="question"> Questions </label>
                                        <input type="text" id="question" defaultValue={this.state.question.id} onChange={(e) => this.handleInputChanges(e)} name="question" className="form-control" placeholder="Enter question" />
                                    </div>
                                    <div className="form-group col-md-12">
                                        <label htmlFor="answer"> Answers </label>
                                        <input type="text" id="answer" defaultValue={this.state.answer.id} onChange={(e) => this.handleInputChanges(e)} name="answer" className="form-control" placeholder="Enter answer" />
                                    </div>


                                    <div className="form-group col-md-4 pull-right">
                                        <button className="btn" type="submit">
                                            Edit QA </button>
                                        {loading &&
                                            <span className="fa fa-circle-o-notch fa-spin" />
                                        }
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                }
            </div>
        )
    }


}



export default QAList;
