import * as React from 'react';

import axios from 'axios';

export interface IValues {
    [key: string]: any;
}
export interface IFormState {
    id: number,
    user: any;
    values: IValues[];
    submitSuccess: boolean;
    loading: boolean;
}

class DeleteUser  extends React.Component<any, any , IFormState >{
    constructor(props: any) {
        super(props);
        this.state = {
            id: "",
            user: {},
            values: [],
            loading: false,
            submitSuccess: false,
        }
    }

    public componentDidMount(): void {
        // axios.get(`http://localhost:5000/customers/${this.state.id}`).then(data => {
        //     this.setState({ user: data.data });
        // })
    }

    private processFormSubmission = async (e: React.FormEvent<HTMLFormElement>): Promise<void> => {
        e.preventDefault();
        this.setState({ loading: true });
        // axios.patch(`http://localhost:5000/customers/${this.state.id}`, this.state.values).then(data => {
        //     this.setState({ submitSuccess: true, loading: false })
        //     setTimeout(() => {
        //         this.props.history.push('/');
        //     }, 1500)
        // })
    }

    private setValues = (values: IValues) => {
        this.setState({ values: { ...this.state.values, ...values } });
    }
    private handleInputChanges = (e: React.FormEvent<HTMLInputElement>) => {
        e.preventDefault();
        this.setValues({ [e.currentTarget.id]: e.currentTarget.value })
    }

    public render() {
        const { submitSuccess, loading } = this.state;
        return (
            <div className="App">
                {this.state.user &&
                    <div>
                        < h1 >  List of Users</h1>


                        <div>
                            <div className={"col-md-12 form-wrapper"}>
                                <h2> Edit User </h2>
                                {submitSuccess && (
                                    <div className="alert alert-info" role="alert">
                                        User's details has been edited successfully </div>
                                )}
                                <form id={"create-post-form"} onSubmit={this.processFormSubmission} noValidate={true}>
                                    <div className="form-group col-md-12">
                                        <label htmlFor="id"> ID </label>
                                        <input type="text" id="id" defaultValue={this.state.user.id} onChange={(e) => this.handleInputChanges(e)} name="id" className="form-control" placeholder="Enter user's id" />
                                    </div>
                                    <div className="form-group col-md-12">
                                        <label htmlFor="public_key"> Public Key </label>
                                        <input type="text" id="public_key" defaultValue={this.state.user.public_key} onChange={(e) => this.handleInputChanges(e)} name="public_key" className="form-control" placeholder="Enter user's public key" />
                                    </div>


                                    <div className="form-group col-md-4 pull-right">
                                        <button className="btn" type="submit">
                                            Edit User </button>
                                        {loading &&
                                            <span className="fa fa-circle-o-notch fa-spin" />
                                        }
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                }
            </div>
        )
    }


}



export default DeleteUser;
