import * as React from "react";
import axios from "axios";
import './AddUser.css'
export interface IValues {
  id: string;
  private_key: string;
}
export interface IFormState {
  [key: string]: any;
  values: IValues[];
  submitSuccess: boolean;
  loading: boolean;
}
class AddUser extends React.Component<any, any> {
  constructor(props: any) {
    super(props);
    this.state = {
      id: "",
      private_key: "",
      values: [],
      loading: false,
      submitSuccess: false,
    };
  }
  private processFormSubmission = (
    e: React.FormEvent<HTMLFormElement>
  ): void => {
    e.preventDefault();
    this.setState({ loading: true });
    const formData = {
      id: this.state.id,
      private_key: this.state.private_key,
    };
    this.setState({
      submitSuccess: true,
      values: [...this.state.values, formData],
      loading: false,
    });
    axios.post(`http://localhost:9000/app/addUser`, formData).then((data) => [
      setTimeout(() => {
        // eslint-disable-next-line
        this.props.history.push("/");
      }, 1500),
    ]);
  };

  private handleInputChanges = (e: React.FormEvent<HTMLInputElement>) => {
    e.preventDefault();
    this.setState({
      [e.currentTarget.name]: e.currentTarget.value,
    });
  };

  public render() {
    const { submitSuccess, loading } = this.state;
    return (
      <div>
        <div className={"col-md-12 form-wrapper"}>
          <h2 > Add User </h2>
          {!submitSuccess && (
            <div className="alert alert-info" role="alert">
              Please add user's public key !
            </div>
          )}
          {submitSuccess && (
            <div className="alert alert-info" role="alert">
              The form was successfully submitted!
            </div>
          )}
          <form
            id={"create-post-form"}
            onSubmit={this.processFormSubmission}
            noValidate={true}
          >
            <div className="form-group col-md-12">
              <label htmlFor="public_key"> Public key</label>
              <input
                type="text"
                id="public_key"
                placeholder="Please enter public key"
                onChange={(e) => this.handleInputChanges(e)}
                name="public_key"
                className="form-control"
              />
            </div>

            <div className="form-group col-md-4 pull-right">
              <button className="btn " type="submit">
                Add User
              </button>
              {loading && <span className="fa fa-circle-o-notch fa-spin" />}
            </div>
          </form>
        </div>
      </div>
    );
  }
}
export default AddUser;
