import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Box,
  IconButton,
  Typography,
} from '@material-ui/core';
import { Close } from '@material-ui/icons';
import create from 'zustand';
const useConfirmDialogStore = create((set) => ({
  message: '',
  onSubmit: undefined,
  close: () => ({ onSubmit: undefined }),
}));

