import * as React from "react";
import { Link } from "react-router-dom";
import axios from "axios";


interface IState {
  QA: any[];
}

export default class EditQA extends React.Component<any, IState> {
  constructor(props: any) {
    super(props);
    this.state = { QA: [] };
  }
  public componentDidMount(): void {

    axios.get(`http://localhost:9000/app/allQA`).then((data) => {
      this.setState({ QA: data.data });
    });
  }
  public editQA(id: any, question: any, answer: any) {
    axios.put(`${`http://localhost:9000/app/updateQA`}?id=${id}`, {question, answer}).then(function (data) {
      // handle success
      console.log({ Users: data.data });
      return data.data;
    })

  }




  public render() {
    const QA = this.state.QA;
    return (
      <div>
        {QA.length === 0 && (
          <div className="text-center">
            <h2>No data found at the moment</h2>
          </div>
        )}
        <div className="container">
          <div className="row">
            <table className="table table-bordered">
              <thead className="thead-light">
                <tr>
                  <th scope="col">Chatbot Question</th>
                  <th scope="col">Chatbot Answer</th>
                </tr>
              </thead>
              <tbody>
                {QA &&
                  QA.map((QA) => (
                    <tr key={QA.id}>
                      <td>{QA.question}</td>
                      <td>{QA.answer}</td>

                      <td>
                        <div className="d-flex justify-content-between align-items-center">
                          <div
                            className="btn-group"
                            style={{ marginBottom: "20px" }}
                          >

                            <button
                              className="btn btn-sm btn-outline-secondary"
                              tsignore
                              onClick={this.editQA}>
                              Edit QA
                            </button>
                          </div>
                        </div>
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}
